import React, { useEffect, useState } from 'react';
import Navbar from './Navbar';
import fetchData from '../../utils/fetchData';
import { GetFormsAPI } from '../../utils/APIRoutes';
import { Navigate, useNavigate } from 'react-router-dom';

export default function Forms() {
    const navigate = useNavigate();
    const [forms, setForms] = useState([]);
    useEffect(() => {
        const user = JSON.parse(localStorage.getItem('user'));
        if (!user) {
            navigate('/login');
        }
        const fetchForms = async () => {
            const forms = await fetchData(GetFormsAPI, 'GET', user.token);
            if (forms.status === true) {
                setForms(forms.forms);
                console.log(forms.forms);
            } else {
                localStorage.removeItem('user');
                navigate('/login');
            }
        };
        fetchForms();
    }, []);
    return (
        <>
            <Navbar />
            <div className="container mx-auto mt-4 flex flex-col space-y-4 justify-center items-center">
                {forms.map((form) => {
                    return (
                        <div key={form._id} className="shadow-md w-2/3 p-4">
                            <h2 className="font-bold ">{form.title}</h2>
                            <p>{form.description}</p>
                            <div className="flex justify-end gap-4 ">
                                <button className="bg-indigo-600">
                                    Delete
                                </button>
                                <button className="bg-violet-600">Edit</button>
                                <button className="bg-violet-600">View</button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </>
    );
}
